targets.expr:
max by (label_kueue_x_k8s_io_pod_group_name, namespace) ((sum_over_time(kube_pod_status_phase{phase="Pending"}[7d]) * kube_pod_status_phase{phase="Pending"}
  + on(namespace, pod) group_left(label_kueue_x_k8s_io_pod_group_name)
    kube_pod_labels{label_kueue_x_k8s_io_pod_group_name=~"(.+)"} * 0
) > 0) * 30

sum by (label_kueue_x_k8s_io_pod_group_name, namespace) ((
  kube_pod_container_resource_requests{resource="nvidia_com_gpu"} 
  * on(namespace, pod) group_left()
    kube_pod_status_phase{phase="Pending"}
  + on(namespace, pod) group_left(label_kueue_x_k8s_io_pod_group_name)
    kube_pod_labels{label_kueue_x_k8s_io_pod_group_name=~"(.+)"} * 0
)) > 0

